import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component{

  constructor(){
    super();

    this.state ={
      monsters: [
        {
          name: 'Frankenstein',
          id: 'MON1'
        },
        {
          name: 'Dracula',
          id: 'MON2'
        },
        {
          name: 'Zombie',
          id: 'MON3'
        }
      ]
    }
  }

  render(){
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            { this.state.str }
          </a>
          <button onClick={ () => this.setState({str : 'Wilson Clicked Me'}) }> Click Me </button>
        </header>

      </div>
    )
  }
}

export default App;
